                    resolve({
                        blob: $event,
                        image,
                        base: reader.result
                    });
                };
                reader.onerror = error => {
                    resolve({
                        base: null
                    });
                };
            }
            catch (e) {
                return null;
            }
        });
    }
    ngOnInit() {
    }
    capturarFile(event) {
        const archivoCapturado = event.target.files[0];
